const totalProductoCorregido = productosInfo.reduce((sum, producto) => sum + producto.total, 0);
const cantidadTotalCorregida = productosInfo.reduce((sum, producto) => sum + producto.cantidad, 0);

// Función para formatear el total con separadores de miles
function formatearPrecio(precio) {
  return new Intl.NumberFormat('es-CO', {
    style: 'currency',
    currency: 'COP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(precio).replace('COP', '').trim();
}

// Crear el resumen del pedido actual
function crearResumenPedido() {
  if (productosInfo.length === 1) {
    const producto = productosInfo[0];
    return `${producto.nombre} x ${producto.cantidad} unidades (${formatearPrecio(producto.total)})`;
  } else {
    const totalItems = cantidadTotalCorregida; // Usar el valor corregido
    const listaProductos = productosInfo.map(p => `${p.nombre} x${p.cantidad}`).join(', ');
    return `${totalItems} productos: ${listaProductos} (${formatearPrecio(totalProductoCorregido)})`;
