// 1. Entradas
const productDelete   = $('Get Update Product').first().json.product;          // nombre a eliminar
const productos_info  = $input.first().json.productos_info;                    // lista original

// 2. Eliminar el producto indicado
const productosSinBorrar = productos_info.filter(
  producto => producto.nombre !== productDelete
);

// 3. Cantidad total resultante
const cantidadTotal = productosSinBorrar.reduce(
  (suma, p) => suma + (p.cantidad || 0),
  0
);

// 4. Función precio unitario según cantidad total
function precioPorEscala(qty) {
  if (qty === 1)               return 60000;
  if (qty === 2)               return 50000;
  if (qty >= 3 && qty <= 5)    return 40000;
  if (qty >= 6)                return 35000;
  return 60000; // fallback
}

// 5. Precio unitario calculado
const precioUnitario = precioPorEscala(cantidadTotal);

// 6. Calcular precio y total por producto
const productosFinales = productosSinBorrar.map(p => ({
  ...p,
  precio: precioUnitario,
  total : precioUnitario * (p.cantidad || 0)
}));

// 7. Total general
const totalGeneral = productosFinales.reduce(
  (suma, p) => suma + (p.total || 0),
  0
);

// 8. Salida para n8n
return [
  {
    json: {
      productos_info : productosFinales,
      cantidad_total : cantidadTotal,
      precio_unitario: precioUnitario,
      total_general  : totalGeneral,
      producto_eliminado: productDelete
    }
  }
];

