// Obtener hora UTC y ajustarla a UTC-5
const ahora = new Date();
const horaUTC = ahora.getUTCHours();
const horaUTC5 = (horaUTC + 24 - 5) % 24;

// Determinar período del día
let periodo = '';

if (horaUTC5 >= 5 && horaUTC5 < 12) {
  periodo = 'mañana';
} else if (horaUTC5 >= 12 && horaUTC5 < 18) {
  periodo = 'tarde';
} else {
  periodo = 'noche';
}

// Retornar período junto con datos necesarios
return [
  {
    json: {
      periodo: periodo,
      hora: horaUTC5,
    }
  }
];

const saludos = [
`🌸 ¡Hola! Gracias por escribir a *Ed Perfumería*  
Estamos encantados de ayudarte a encontrar la fragancia ideal.`,

